mapper.select:
select STANDARD_ID standardId, STANDARD_CODE standardCode, STANDARD_NAME standardName, STANDARD_STEP standardStep, EQUITMET_TYPE equitmetType, CLEAN_TOOLS cleanTools, STANDARD_REQUIREMENT standardRequirement, MARK_SCALE markScale, CHECK_FLAG checkFlag, UPLOAD_PIC_FLAG uploadPicFlag, REMARK1 remark1, REMARK2 remark2, REMARK3 remark3, CREATE_EMP createEmp, CREATE_DATE createDate, UPDATE_DATE updateDate, UPDATE_EMP updateEmp, VERSION version from tb_area_standard where 1=1
AND STANDARD_NAME LIKE CONCAT('%',#{standardName},'%')
AND CHECK_FLAG=#{checkFlag}
AND UPLOAD_PIC_FLAG=#{uploadPicFlag}
ORDER BY CREATE_DATE DESC
