mapper.select:
select EQUIP_CHARGE_ID equipChargeId, EQUIP_ID equipId, EQUIP_NAME equipName, EQUIP_NO equipNo, EQUIP_TYPE equipType, EQUIP_MODEL equipModel, DATE_FORMAT(START_CHARGE_TIME,'%Y-%m-%d %H:%i:%S') startChargeTime, DATE_FORMAT(END_CHARGE_TIME,'%Y-%m-%d %H:%i:%S') endChargeTime, TOTAL_CHARGE_TIME totalChargeTime, AREA area, PERSON_NAME personName, PERSON_MOBILE personMobile, STATUS status from TB_EQUIP_CHARGE where 1=1
and EQUIP_NO = #{obj.equipNo}
and START_CHARGE_TIME > substring(#{obj.startChargeTime},1,19)
and START_CHARGE_TIME < substring(#{obj.startChargeTime},22,19)
and STATUS = #{obj.status}
ORDER BY START_CHARGE_TIME
