mapper.select:
select EQUIP_MAINT_ID equipMaintId, EQUIP_ID equipId, EQUIP_NO equipNo, EQUIP_NAME equipName, EQUIP_TYPE equipType, EQUIP_MODEL equipModel, PARTS_NAME partsName, PARTS_NO partsNo, MAINT_TYPE maintType, EQUIP_CYCLE equipCycle, DATE_FORMAT(MAINT_TIME,'%Y-%m-%d %H:%i:%S') maintTime, MAINT_RECORD maintRecord, MAINT_STATE maintState, MAINT_DESC maintDesc, REMARK remark from TB_EQUIP_MAINT where 1=1
and MAINT_TYPE = #{maintType}
and EQUIP_NAME = #{equipName}
and PARTS_NAME = #{partsName}
ORDER BY MAINT_TIME
