mapper.select:
select EQUIP_REPAIR_ID equipRepairId, EQUIP_ID equipId, EQUIP_NAME equipName, EQUIP_NO equipNo, EQUIP_TYPE equipType, EQUIP_MODEL equipModel, DATE_FORMAT(REPAIR_TIME,'%Y-%m-%d %H:%i:%S') repairTime, PERSON_NAME personName, PERSON_MOBILE personMobile, EQUIP_DESC equipDesc, STATUS status from TB_EQUIP_REPAIR where 1=1
and EQUIP_TYPE = #{obj.equipType}
and EQUIP_NAME = #{obj.equipName}
and REPAIR_TIME > #{obj.repairTime}
and STATUS = #{obj.status}
ORDER BY REPAIR_TIME

mapper.update:
update TB_EQUIP_REPAIR
SET
STATUS = 'REPAIRED',
UPDATE_EMP = #{updateEmp}
WHERE
EQUIP_REPAIR_ID = #{equipRepairId}
