mapper.select:
select a.EQUIP_REPAIR_ID equipRepairId, a.EQUIP_ID equipId, a.EQUIP_NAME equipName, a.EQUIP_NO equipNo, b.DESC1_ equipType, a.EQUIP_MODEL equipModel, DATE_FORMAT(a.REPAIR_TIME,'%Y-%m-%d %H:%i:%S') repairTime, a.PERSON_NAME personName, a.PERSON_MOBILE personMobile, a.EQUIP_DESC equipDesc, a.STATUS status from TB_EQUIP_REPAIR a LEFT JOIN tbpubcode01 b ON a.EQUIP_TYPE = b.DESC_ where b.CONFIG_CODE_ = 'EQUIP_TYPE'
and a.EQUIP_TYPE = #{obj.equipType}
and a.EQUIP_NAME = #{obj.equipName}
and a.REPAIR_TIME > #{obj.repairTime}
and a.STATUS = #{obj.status}
ORDER BY a.REPAIR_TIME

mapper.update:
update TB_EQUIP_REPAIR
SET
STATUS = 'REPAIRED',
UPDATE_EMP = #{updateEmp}
WHERE
EQUIP_REPAIR_ID = #{equipRepairId}
