mapper.select:
select EQUIP_USE_ID equipUseId, EQUIP_ID equipId, EQUIP_NAME equipName, EQUIP_NO equipNo, EQUIP_TYPE equipType, EQUIP_MODEL equipModel, DATE_FORMAT(START_USE_TIME,'%Y-%m-%d %H:%i:%S') startUseTime, DATE_FORMAT(END_USE_TIME,'%Y-%m-%d %H:%i:%S') endUseTime, TOTAL_USE_TIME totalUseTime, AREA area, PERSON_NAME personName, PERSON_MOBILE personMobile, STATUS status from TB_EQUIP_USE where 1=1
and EQUIP_NO = #{obj.equipNo}
and START_USE_TIME > #{obj.startUseTime}
and STATUS = #{obj.status}
ORDER BY START_USE_TIME
