mapper.select:
SELECT PLAN_EVENT_ID planEventId, AREA_CODE areaCode, (SELECT A.AREA_NAME FROM tb_area A WHERE A.AREA_CODE = T.AREA_CODE) areaName, STANDARD_CODE standardCode, (SELECT A.STANDARD_NAME FROM tb_area_standard A WHERE A.STANDARD_CODE = T.STANDARD_CODE) standardName, EVENT_TYPE eventType, GUARANTEE_TYPE guaranteeType, REMARK1 remark1, REMARK2 remark2, REMARK3 remark3, CREATE_EMP createEmp, CREATE_DATE createDate, UPDATE_EMP updateEmp, UPDATE_DATE updateDate, VERSION version, CHECK_FLAG checkFlag, UPLOAD_PIC_FLAG uploadPicFlag, START_SCAN_FLAG startScanFlag, END_SCAN_FLAG endScanFlag FROM tb_plan_event T
AND AREA_CODE = #{param.areaCode}
AND STANDARD_CODE = #{param.standardCode}
AND EVENT_TYPE = #{param.eventType}
AND GUARANTEE_TYPE = #{param.guaranteeType}

mapper.update:
        UPDATE tb_plan_event




                        WHEN PLAN_EVENT_ID = #{item.planEventId} THEN #{item.checkFlag}






                        WHEN PLAN_EVENT_ID = #{item.planEventId} THEN #{item.uploadPicFlag}






                        WHEN PLAN_EVENT_ID = #{item.planEventId} THEN #{item.startScanFlag}






                        WHEN PLAN_EVENT_ID = #{item.planEventId} THEN #{item.endScanFlag}






                        WHEN PLAN_EVENT_ID = #{item.planEventId} THEN #{item.updateEmp}





					WHEN PLAN_EVENT_ID = #{item.planEventId} THEN #{item.updateDate}




                    WHEN PLAN_EVENT_ID = #{item.planEventId} THEN VERSION + 1



        WHERE PLAN_EVENT_ID IN (

            #{item.planEventId}

        )
