mapper.select:
SELECT PLAN_ID planId, AREA_CODE areaCode, (SELECT A.AREA_NAME FROM tb_area A WHERE A.AREA_CODE = T.AREA_CODE) areaName, STANDARD_CODE standardCode, (SELECT A.STANDARD_NAME FROM tb_area_standard A WHERE A.STANDARD_CODE = T.STANDARD_CODE) standardName, PLAN_TIME_TYPE planTimeType, MONTH month, DAY day, WEEK week, START_TIME startTime, END_TIME endTime, PREV_TASK_DONE_DATE prevTaskDoneDate, REMARK1 remark1, REMARK2 remark2, REMARK3 remark3, CREATE_EMP createEmp, CREATE_DATE createDate, UPDATE_EMP updateEmp, UPDATE_DATE updateDate, VERSION version, CHECK_FLAG checkFlag, UPLOAD_PIC_FLAG uploadPicFlag, START_SCAN_FLAG startScanFlag, END_SCAN_FLAG endScanFlag FROM tb_plan T
AND AREA_CODE = #{param.areaCode}
AND STANDARD_CODE = #{param.standardCode}
AND PLAN_TIME_TYPE = #{param.planTimeType}
AND START_TIME >= substring(#{param.time},1,8) AND END_TIME <= substring(#{param.time},12,19)

mapper.update:
        UPDATE tb_plan




                        WHEN PLAN_ID = #{item.planId} THEN #{item.checkFlag}






                        WHEN PLAN_ID = #{item.planId} THEN #{item.uploadPicFlag}






                        WHEN PLAN_ID = #{item.planId} THEN #{item.startScanFlag}






                        WHEN PLAN_ID = #{item.planId} THEN #{item.endScanFlag}






                        WHEN PLAN_ID = #{item.planId} THEN #{item.updateEmp}





					WHEN PLAN_ID = #{item.planId} THEN #{item.updateDate}




                    WHEN PLAN_ID = #{item.planId} THEN VERSION + 1



        WHERE PLAN_ID IN (

            #{item.planId}

        )
