mapper.select:
SELECT T.PLAN_ID planId, T.AREA_CODE areaCode, (SELECT A.AREA_NAME FROM tb_area A WHERE A.AREA_CODE = T.AREA_CODE) areaName, T.STANDARD_CODE standardCode, (SELECT A.STANDARD_NAME FROM tb_area_standard A WHERE A.STANDARD_CODE = T.STANDARD_CODE) standardName, T.PLAN_TIME_TYPE planTimeType, T.MONTH month, T.DAY day, T.WEEK week, T.START_TIME startTime, T.END_TIME endTime, T.PREV_TASK_DONE_DATE prevTaskDoneDate, T.REMARK1 remark1, T.REMARK2 remark2, T.REMARK3 remark3, T.CREATE_EMP createEmp, T.CREATE_DATE createDate, T.UPDATE_EMP updateEmp, T.UPDATE_DATE updateDate, T.VERSION version, T.CHECK_FLAG checkFlag, T.UPLOAD_PIC_FLAG uploadPicFlag, T.START_SCAN_FLAG startScanFlag, T.END_SCAN_FLAG endScanFlag, T.program_code programCode, p.program_name programName FROM tb_plan T LEFT JOIN tb_program p ON T.program_code = p.program_code
AND T.AREA_CODE = #{param.areaCode}
AND T.STANDARD_CODE = #{param.standardCode}
AND T.PLAN_TIME_TYPE = #{param.planTimeType}
AND T.START_TIME >= substring(#{param.time},1,8) AND T.END_TIME <= substring(#{param.time},12,19)
AND T.program_code = #{param.programCode}

SELECT
	AREA_CODE areaCode,
	STANDARD_CODE standardCode,
	PLAN_TIME_TYPE planTimeType,
	MONTH month,
	DAY day,
	WEEK week,
	START_TIME startTime,
	END_TIME endTime,
	PREV_TASK_DONE_DATE prevTaskDoneDate,
	CHECK_FLAG checkFlag,
	UPLOAD_PIC_FLAG uploadPicFlag,
	START_SCAN_FLAG startScanFlag,
	END_SCAN_FLAG endScanFlag
FROM
	tb_plan T
WHERE
	AREA_CODE = #{areaCode}
	AND STANDARD_CODE = #{standardCode}
	AND PLAN_TIME_TYPE = #{planTimeType}
	AND MONTH = #{month}
	AND DAY = #{day}
	AND WEEK = #{week}
	AND START_TIME = #{startTime}
	AND END_TIME = #{endTime}
	AND PREV_TASK_DONE_DATE = #{prevTaskDoneDate}
	AND CHECK_FLAG = #{checkFlag}
	AND UPLOAD_PIC_FLAG = #{uploadPicFlag}
	AND START_SCAN_FLAG = #{startScanFlag}
	AND END_SCAN_FLAG = #{endScanFlag}

mapper.update:
        UPDATE tb_plan




                        WHEN PLAN_ID = #{item.planId} THEN #{item.checkFlag}






                        WHEN PLAN_ID = #{item.planId} THEN #{item.uploadPicFlag}






                        WHEN PLAN_ID = #{item.planId} THEN #{item.startScanFlag}






                        WHEN PLAN_ID = #{item.planId} THEN #{item.endScanFlag}






                        WHEN PLAN_ID = #{item.planId} THEN #{item.updateEmp}





					WHEN PLAN_ID = #{item.planId} THEN #{item.updateDate}




                    WHEN PLAN_ID = #{item.planId} THEN VERSION + 1



        WHERE PLAN_ID IN (

            #{item.planId}

        )
