mapper.select:
SELECT m.TASK_ID AS taskId, m.TASK_CODE AS taskCode, m.PLAN_ID AS planId, m.TASK_TYPE AS taskType, m.POSITION_CODE AS positionCode, m.POSITION_NAME AS positionName, m.AREA_CODE AS areaCode, m.AREA_NAME AS areaName, m.STANDARD_CODE AS standardCode, m.STANDARD_NAME AS standardName, m.SHIFT AS shift, m.SPEC AS spec, m.EMPLOYEE_CODE AS employeeCode, m.EMPLOYEE_NAME AS employeeName, m.EMPLOYEE_TEL AS employeeTel, DATE_FORMAT(m.COMPLETE_TIME,'%Y-%m-%d %T') AS completeTime, DATE_FORMAT(m.START_TIME,'%Y-%m-%d %T') AS startTime, DATE_FORMAT(m.END_TIME,'%Y-%m-%d %T') AS endTime, m.TASK_STATUS AS taskStatus, m.CHECK_STATUS AS checkStatus, m.TASK_SCORE AS taskScore, m.CHECK_FLAG AS checkFlag, m.UPLOAD_PIC_FLAG AS uploadPicFlag, m.TASK_ADVICE AS taskAdvice, m.TASK_FINISH_DESC AS taskFinishDesc, m.DISQUALIFIED_REASON AS disqualifiedReason, m.FEEDBACK_IMG_URL AS feedbackImgUrl, m.CHECK_IMG_URL AS checkImgUrl, m.TASK_IMG_URL AS taskImgUrl, m.LINE_NO AS lineNo, m.REMARK1 AS remark1, m.REMARK2 AS remark2, m.REMARK3 AS remark3, DATE_FORMAT(m.CREATE_DATE,'%Y-%m-%d %T') AS createDate FROM tb_task m WHERE m.TASK_STATUS !='CANCLE'
AND m.EMPLOYEE_CODE =#{employeeCode}
AND m.TASK_STATUS=#{taskStatus}
AND m.TASK_Type=#{taskType}
AND m.SHIFT=#{shift}
ORDER BY m.CREATE_DATE DESC

SELECT STANDARD_CODE standardCode,STANDARD_NAME standardName FROM tb_area_standard WHERE 1=1
AND STANDARD_CODE =#{standardCode}
ORDER BY STANDARD_CODE

SELECT EMPLOYEE_CODE employeeCode,EMPLOYEE_NAME employeeName,EMPLOYEE_TEL employeeTel 
FROM tb_task_arrangement 
WHERE SHIFT =#{shift} 
AND MONTH=#{month} 
AND POSITION_CODE=#{positionCode} 
AND DELETE_FLAG ='NORMAL'

SELECT ARRANGEMENT_ID AS arrangementId, SHIFT AS shift, EMPLOYEE_CODE AS employeeCode, EMPLOYEE_NAME AS employeeName, EMPLOYEE_TEL AS employeeTel, POSITION_CODE AS positionCode, POSITION_NAME AS positionName, MONTH AS month FROM tb_task_arrangement WHERE MONTH=#{month} AND EMPLOYEE_CODE=#{employeeCode}
AND SHIFT =#{shift}
AND DELETE_FLAG ='NORMAL'
