mapper.select:
select STOREROOM_ID, STOREROOM, WAREHOUSE_REQDETAIL_ID, a.WAREHOUSE_REQU_ID, a.WAREHOUSE_STOCK_ID, REQU_LOC, REQU_LOC_NAME, APPLY_NUM, REAL_NUM, MIN_UNIT, a.REQU_STATUS, a.REQU_REMARK, b.REQU_NAME from tb_warehouse_reqdetail a LEFT JOIN tb_warehouse_requ b on a.WAREHOUSE_REQU_ID = b.WAREHOUSE_REQU_ID LEFT JOIN tb_warehouse_stock c on a.WAREHOUSE_STOCK_ID = c.WAREHOUSE_STOCK_ID where a.REQU_STATUS = 'RECEIVE'
and c.STOREROOM_ID = #{storeroomId}
and a.REQU_LOC_NAME like concat('%',#{requLocName},'%')
and c.GOODS_NO like concat('%',#{goodsNo},'%')
and c.GOODS_NAME like concat('%',#{goodsName},'%')
and b.REQU_NAME like concat('%',#{requName},'%')
and DATE(b.REQU_TIME) >= #{startDate}
and DATE(b.REQU_TIME) <= #{endDate}
ORDER BY b.REQU_TIME desc
