mapper.select:
SELECT WORK_ORDER_ID workOrderId, WORK_ORDER_NAME workOrderName, WORK_ORDER_TYPE workOrderType, WORK_START_TIME workStartTime, WORK_END_TIME workendTime, WORK_TOTAL_TIME workTotalTime, WORK_ORDER_STATUS workOrderStatus, REMARK remark FROM TB_WORK_ORDER WHERE 1=1
and WORK_ORDER_TYPE = #{workOrderType}
and WORK_ORDER_NAME = #{workOrderName}
and WORK_ORDER_STATUS = #{workOrderStatus}
