select id_ id, num_ num, code_ code, pcode_ pcode, name_ name, icon_ icon, url_ url, pnum_ pnum, seq_ seq, levels_ levels, isMenu_ ismenu, (CASE WHEN isMenu_ ='0' THEN '否' WHEN isMenu_ ='1' THEN '是' END) AS isMenuName, status_ statusName from tb_menu where status_ = 1
and (name_ like CONCAT('%',#{name},'%') or code_ like CONCAT('%',#{name},'%'))
and levels_ = #{levels}
and code_ = #{code} or pcode_ = #{code}
ORDER BY code_

SELECT m1.code_ AS id, ( CASE WHEN (m2.code_ = 0 OR m2.code_ IS NULL) THEN 0 ELSE m2.code_ END ) AS pId, m1.name_ AS NAME, ( CASE WHEN (m2.code_ = 0 OR m2.code_ IS NULL) THEN 'true' ELSE 'false' END ) as isOpen, ( CASE WHEN (m3.code_ = 0 OR m3.code_ IS NULL) THEN 'false' ELSE 'true' END ) "checked" FROM tb_menu m1 LEFT JOIN tb_menu m2 ON m1.pcode_ = m2.code_ left join ( SELECT code_ FROM tb_menu WHERE code_ IN
#{i}
) m3 on m1.code_ = m3.code_ ORDER BY m1.code_ ASC
